import React from 'react'
import '../Styles/Table-Styles.css'
import axios from 'axios';


export class Table extends React.Component {
    constructor(props) {
       super(props)
       this.state = {
          instances: []
       }
    }
    componentDidMount () {
        axios.get(`http://localhost:9090/?region=us-east-2`)
          .then(res => {
            const instances = res.data;
            this.setState({ instances : instances });
          })
      }


    renderTableHeader() {
       let header = Object.keys(this.state.instances[0])
       console.log(header)
       return header.map((key, index) => {
          return <th key={index}>{key.toUpperCase()}</th>
       })
    }

    renderTableData() {
       return this.state.instances.map((instance, index) => {
          const { InstanceId, InstanceType, ImageId, AvailabilityZone, State} = instance 
          return (
             <tr key={InstanceId}>
                <td>{InstanceId}</td>
                <td>{ImageId}</td>
                <td>{InstanceType}</td>
                <td>{AvailabilityZone}</td>
                <td>{State}</td>
             </tr>
          )
       })
    }

    render() {
       return (
          <div className="table-container">
             <h1 id='title'>AWS EC2 Instances</h1>
             <table id='instances'>
                <tbody>
                   <tr>{this.renderTableHeader()}</tr>
                   {this.renderTableData()}
                </tbody>
             </table>
          </div>
       )
    }
 }
