import React from 'react'
import '../Styles/Table-Styles.css'


export class Table extends React.Component {
    constructor(props) {
       super(props)
       this.state = {
          instances: [
            {
                "InstanceId": "i-0c1ecee2dcacbc06e",
                "ImageId": "ami-0c6e5500f4f560354",
                "InstanceType": "t2.micro",
                "AvailabilityZone": "us-east-2c",
                "State": "stopped"
            },
            {
                "InstanceId": "i-02b4fb10637098855",
                "ImageId": "ami-026dea5602e368e96",
                "InstanceType": "t2.micro",
                "AvailabilityZone": "us-east-2a",
                "State": "stopped"
            },
            {
                "InstanceId": "i-07957040142b2107d",
                "ImageId": "ami-0c6e5500f4f560354",
                "InstanceType": "t2.micro",
                "AvailabilityZone": "us-east-2a",
                "State": "stopped"
            },
            {
                "InstanceId": "i-064df3e6bfb3d5dff",
                "ImageId": "ami-0c6e5500f4f560354",
                "InstanceType": "t2.micro",
                "AvailabilityZone": "us-east-2b",
                "State": "stopped"
            }
        ]
       }
    }

    renderTableHeader() {
       let header = Object.keys(this.state.instances[0])
       return header.map((key, index) => {
          return <th key={index}>{key.toUpperCase()}</th>
       })
    }

    renderTableData() {
       return this.state.instances.map((instance, index) => {
          const { InstanceId, InstanceType, ImageId, AvailabilityZone, State} = instance 
          return (
             <tr key={InstanceId}>
                <td>{InstanceId}</td>
                <td>{InstanceType}</td>
                <td>{ImageId}</td>
                <td>{AvailabilityZone}</td>
                <td>{State}</td>
             </tr>
          )
       })
    }

    render() {
       return (
          <div className="table-container">
             <h1 id='title'>AWS EC2 Instances</h1>
             <table id='instances'>
                <tbody>
                   <tr>{this.renderTableHeader()}</tr>
                   {this.renderTableData()}
                </tbody>
             </table>
          </div>
       )
    }
 }
