import React from 'react'
import '../Styles/Table-Styles.css'
import {Dropdown} from 'react-bootstrap';
import axios from 'axios';
import { CheckboxToggle } from 'react-rainbow-components';


export class Table extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
           instances: [],
           region: 'us-east-2',
        }
     }
    async getInstances() {
        await axios.get(`http://localhost:8080/?region=${this.state.region}`)
        .then(res => {
        const instances = res.data;
        this.setState({ instances : instances });   
        }).catch(err => console.log(err))
    }
    async componentDidMount () {
      await this.getInstances();
    }

    selectRegion (key) {        
        this.setState({
            region: key
        }, () => this.getInstances() )
    }
    startStopInstance () {

    }
    render() {
        const region = [
            'ap-south-1',
            'eu-west-1',
            'us-east-2',
            'ap-southeast-1',
            'ap-southeast-2',
            'eu-central-1',
            'ap-northeast-2',
            'ap-northeast-1',
            'us-east-1',
            'sa-east-1',
            'us-west-1',
            'us-west-2'
        ];
       return (
          <div className="table-container">
              <Dropdown>
                <Dropdown.Toggle variant="success" id="dropdown-basic">
                    Region
                </Dropdown.Toggle>
                <Dropdown.Menu>
                    {region.map(item => {
                        return  <Dropdown.Item eventKey={item} onSelect={eventKey => this.selectRegion(eventKey)}>{item}</Dropdown.Item>
                    })}
                </Dropdown.Menu>
                </Dropdown>
             <h1 id='title'>AWS EC2 Instances</h1>
             <table id='instances'>
                <tbody>
                   <tr>
                       <th>Instance ID</th>
                       <th>Image ID</th>
                       <th>Instance Type</th>
                       <th>Availabilty Status</th>
                       <th>Status</th>
                   </tr>
                   {this.state.instances.map((instance, index) => {
                       let value = instance.State === 'stopped' ? false : true;
                        return (
                            <tr key={instance.InstanceID}>
                                <td>{instance.InstanceID}</td>
                                <td>{instance.ImageID}</td>
                                <td>{instance.InstanceType}</td>
                                <td>{instance.AvailabilityZone}</td>
                                <td>
                                    <CheckboxToggle
                                        label={instance.State}
                                        value={value}
                                        onChange={value => console.log(value)}
                                    />
                                </td>
                            </tr>
                        );
                })}
                </tbody>
             </table>
          </div>
       )
    }
 }
