import React, { Component } from 'react'
import {Form, Button} from 'react-bootstrap'
import '../Styles/form-style.css'
export class Forms extends Component {
    constructor(props) {
        super(props)
        this.state = {
           validated: false,
        }
     }
    handleSubmit = (event) => {
        const form = event.currentTarget;
        console.log(form)
        if (form.checkValidity() === false) {
          event.preventDefault();
          event.stopPropagation();
        }
        this.setState({
            validated: true
        })
      };
    render() {
        return (
            <div className="form-container">
            <h2 className="h2">Create Instance</h2>
            <Form className="align-items-center" noValidate validated={this.state.validated} onSubmit={(event) => this.handleSubmit(event)}>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <Form.Label>Region</Form.Label>
                    <Form.Control as="select">
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option>
                    </Form.Control>
                </Form.Group>
              <Form.Group  md="4" controlId="validationImageId">
                <Form.Label>Image ID (AMI ID)</Form.Label>
                <Form.Control
                  required
                  type="text"
                  placeholder="Image ID"
                />
                <Form.Control.Feedback type="invalid">Enter a valid Image ID</Form.Control.Feedback>
              </Form.Group>
              <Form.Group  md="4" controlId="validationTagName">
                <Form.Label>Tag Key</Form.Label>
                <Form.Control
                  required
                  type="text"
                  placeholder="Tag Key"
                />
                <Form.Control.Feedback type="invalid">Enter Tag Key</Form.Control.Feedback>
              </Form.Group>
              <Form.Group  md="4" controlId="validationTagValue">
                <Form.Label>Tag Value</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Tag Value"
                    required
                  />
                  <Form.Control.Feedback type="invalid">Enter Tag Value</Form.Control.Feedback>
              </Form.Group>
            <Button type="submit">Create Instance</Button>
          </Form>
          </div>
        );
      }
}
