import React from 'react'
import '../Styles/Table-Styles.css'
import axios from 'axios';


export class Table extends React.Component {
    constructor(props) {
       super(props)
       this.state = {
          instances: []
       }
    }
    componentDidMount () {
        console.log('Called component did mount')
        axios.get(`http://localhost:8080/?region=us-east-2`)
          .then(res => {
            console.log(res.data)
            const instances = res.data;
            this.setState({ instances : instances });   
          }).catch(err => console.log(err))
      }


    renderTableHeader () {
        console.log( 'Called table header')
       let header = Object.keys(this.state.instances[0])
       return header.map((key, index) => {
          return <th key={index}>{key.toUpperCase()}</th>
       })
    }


    render() {
       return (
          <div className="table-container">
             <h1 id='title'>AWS EC2 Instances</h1>
             <table id='instances'>
                <tbody>
                   <tr>{this.renderTableHeader().bind(this)}</tr>
                   {this.state.instances.map((instance, index) => {
                        return (
                            <tr key={instance.InstanceId}>
                                <td>{instance.InstanceId}</td>
                                <td>{instance.ImageId}</td>
                                <td>{instance.InstanceType}</td>
                                <td>{instance.AvailabilityZone}</td>
                                <td>{instance.State}</td>
                            </tr>
                        );
                })}
                </tbody>
             </table>
          </div>
       )
    }
 }
